mapper.insert:
INSERT INTO `order` (
    order_no, user_id, activity_id, product_id, amount,
    status, create_time, update_time
) VALUES (
    #{orderNo}, #{userId}, #{activityId}, #{productId}, #{amount},
    #{status}, #{createTime}, #{updateTime}
)

mapper.select:
SELECT *
FROM `order`
WHERE create_time <
      #{timeoutThreshold}
  AND status = #{status}
